import { Directive } from '@angular/core';
import { AbstractControl, NG_VALIDATORS, ValidationErrors, Validator } from '@angular/forms';
import { crossfieldValidation } from './crossfield.validation';

@Directive({
  selector: '[appFormDirective]',
  providers:[{
    provide: NG_VALIDATORS,
    useExisting:FormDirectiveDirective,
    multi:true
  }]
})
export class FormDirectiveDirective implements Validator {
validate(control: AbstractControl<any,any>): ValidationErrors{
  return crossfieldValidation(control);
}
}
