import { Component, OnInit } from '@angular/core';
import { Router } from '@angular/router';

import { Flight } from '../flight';
import { FlightServiceService } from '../services/flight-service.service';

@Component({
  selector: 'app-book',
  templateUrl: './book.component.html',
  styleUrls: ['./book.component.css']
})


export class BookComponent implements OnInit {

  //book: bookdetails[];
  flight: Flight=new Flight();
  flights :Flight[];

  public show:boolean = false;


  constructor( private flightService: FlightServiceService, private router: Router){
    this.flights=[]
    this.flight.origin=this.route.snapshot.paramMap.get('origin')!
    this.flight.destination=this.route.snapshot.paramMap.get('destination')!
    this.flight.flightdate=this.route.snapshot.paramMap.get('date')!
    // this.flight.flightdate=this.route.snapshot.paramMap.get('date')!
    // this.flight.flightdate=this.route.snapshot.paramMap.get('date')!



    this.getFlightDetailsToBook()

  }

  ngOnInit(){}

  toggle(){
    this.show = !this.show;
  }

  private getFlightDetailsToBook(){
   this.flightService.getAvailableFlights(this.flight.origin, this.flight.destination, this.flight.flightdate).subscribe(data=>{
    this.flights = data;
    console.log(this.flights);
   }, error => console.error(error));



  }




}
<div>
    <div class="container-fluid">
        <table class="table table-borderless">
            <thead>
            <tr>
                <th>Origin</th>
                <th>Destination</th>
                <th>Flight Date</th>

            </tr>
            </thead>
            <tr>
                <td class="align-middle"> {{flight.origin}}</td>
                <td class="align-middle"> {{flight.destination}}</td>
                <td class="align-middle"> {{flight.flightdate}}</td>
                <td>
                <button (click)="toggle()" class ="btn btn-info">Details</button>
                </td>
            </tr>
        </table>
        <ng-container *ngIf="show">
            <div class="container" style="margin:0;text-align: center">
                <h6>Price Details</h6>
                <label> Fare </label>
                <textarea>{{flight.fare.fare}}</textarea>
                <h6> Flight Details</h6>
                <label>Flight Number</label>
                <textarea>{{flight.flightId}}</textarea>
                <label> No. of seats</label>
                <textarea>{{flight.inventory.count}}</textarea>
            </div>
        </ng-container>

    <div class="mx-auto position-absolute bottom-0 start-50 translate-middle-x">
        <button class="btn btn-primary align-text-bottom" type="button">Add Passenger Details</button>
    </div>
</div>
