import { Component, OnInit } from '@angular/core';
import { Router, ActivatedRoute } from '@angular/router';
import { Flight } from '../flight';
import { FlightServiceService } from '../services/flight-service.service';
@Component({
  selector: 'app-book',
  templateUrl: './book.component.html',
  styleUrls: ['./book.component.css'],
})
export class BookComponent implements OnInit {
  flightId: number | undefined;
  flight = new Flight();
  public show: boolean = false;
  constructor(
    private flightService: FlightServiceService,
    private router: Router,
    private route: ActivatedRoute
  ) {}
  ngOnInit(): void {
    this.flightId = this.route.snapshot.params['flightId'];
    this.flightService.getFlightByFlightId(this.flightId).subscribe(
      (data) => {
        this.flight = data;
        console.log(this.flight);
      },
      (error) => console.log(error)
    );
  }
  toggle() {
    this.show = !this.show;
  }
  passengerDetails(flightId: number | undefined) {
    this.router.navigate(['passenger-details', flightId]);
  }
}
<div>
    <div class="container-fluid" style="height:100vh">
       <h3 style = "text-align: center">{{flight.origin}}  &nbsp; {{flight.destination}}  &nbsp; {{flight.flightDate}}</h3><br>
       <b><table class="table table-borderless">
            <thead>
            <tr>
                <th>Origin</th>
                <th>Destination</th>
                <th>Flight Date</th>
                <th>Flight Number</th>


            </tr>
            </thead>
            <tr>
                <td class="align-middle"> {{flight.origin}}</td>
                <td class="align-middle"> {{flight.destination}}</td>
                <td class="align-middle"> {{flight.flightDate}}</td>
                <td>
                <button (click)="toggle()" class ="btn btn-info">Details</button>
                </td>
            </tr>
        </table></b>

        <ng-container *ngIf="show">
            <div class="container" style="margin:0;text-align: center">
                <h6>Price Details</h6>
                <label> Fare </label>
                <textarea readonly>{{flight.fare.fare}}</textarea>
                <h6> Flight Details</h6>
                <label>Flight Number</label>
                <textarea readonly>{{flight.flightId}}</textarea>
                <label> No. of seats</label>
                <textarea readonly>{{flight.inventory.count}}</textarea>
            </div>
        </ng-container>

    <div class="mx-auto position-absolute bottom-0 start-50 translate-middle-x">
        <button (click)="passengerDetails(flight.flightId)" class="btn btn-primary align-text-bottom" type="button">Add Passenger Details</button>
    </div>
</div>
