import { Component, OnInit, Input } from '@angular/core';
import { FormArray, FormControl, FormGroup } from '@angular/forms';
import { Passenger } from '../passenger';
import { BookServiceService } from '../services/book-service.service';
import { ActivatedRoute, Router } from '@angular/router';
import { Book } from '../book';
import { Flight } from '../flight';
import { FlightServiceService } from '../services/flight-service.service';
@Component({
  selector: 'app-passenger-details',
  templateUrl: './passenger-details.component.html',
  styleUrls: ['./passenger-details.component.css'],
})
export class PassengerDetailsComponent implements OnInit {
  passenger: Passenger = { id: 0, firstName: '', lastName: '', gender: '' };
  passengers: Passenger[] = [];
  book: Book = {
    flight: Flight,
    bookId: 0,
    bookingDate: '',
    passenger: this.passengers,
    status:"",
    totalFare:0
  };
  flight!: Flight;
  flightId: number|undefined 
  constructor(
    private bookservice: BookServiceService,
    private router: Router,
    private route: ActivatedRoute,
    private flightService: FlightServiceService
  ) {

   
  }
  ngOnInit(): void {
    this.passenger = new Passenger();
    this.passengers.push(this.passenger);
    console.log(this.flight);
    this.flightId = JSON.parse(this.route.snapshot.paramMap.get('Flight')!);
    this.flightService.getFlightByFlightId(this.flightId).subscribe(data=>{
      this.flight=data;
      console.log(data);
    })
  }
  addForm() {
    this.passenger = new Passenger();
    this.passengers.push(this.passenger);
  }
  removeForm(index: number) {
    this.passengers.splice(index);
  }
  onSubmit() {
    console.log(this.passengers);
  }
  onSave() {
    
    this.book.flight = this.flight;
    console.log(this.flight);
    this.bookservice
      .saveBook(this.flight, this.passengers)
      .subscribe((data) => {
        console.log(this.book);
        this.book= data;
        
        this.router.navigate(['/book-details', this.book.bookId, this.passengers]);
      });
  }
}
<h4>Passenger Details</h4>
<form (ngSubmit)="onSubmit()">

  <div class="container">
    <div
      class="row col-auto"
      *ngFor="let passenger of passengers; let i = index"
    >
      <div class="form-group col-auto">
        <label>First Name</label>
        <input
          type="text"
          class="form-control"
          name="firstname{{ i }}"
          [(ngModel)]="passenger.firstName"
        />
      </div>
      
      <div class="form-group col-auto">
        <label>Last Name</label>
        <input
          type="text"
          class="form-control"
          name="lastname{{ i }}"
          [(ngModel)]="passenger.lastName"
        />
      </div>
  
      <div class="form-group col-auto">
        <label>Gender</label>
        <input
          type="text"
          class="form-control"
          name="gender{{ i }}"
          [(ngModel)]="passenger.gender"
        />
      </div>
      <div
        class="form-group col-auto"
        style="padding: 24px 10px"
        *ngIf="i != 0"
      >
        
        <span (click)="removeForm(i)" class="btn btn-danger mb-2">-</span>
      </div>
    
    </div>
    
  </div>
  
  <div class="form-group col-md-2">
    <span (click)="addForm()" class="btn btn-success">+</span>
    <button class="btn btn-primary">Submit</button>
  </div>
</form>
<button (click)="onSave()" class="btn btn-success">Confirm Booking</button>
