import { Component , Input } from '@angular/core';
import { Router } from '@angular/router';
import { Flight } from '../flight';
import {FlightServiceService}  from '../services/flight-service.service'

@Component({
  selector: 'app-flight-list',
  templateUrl: './flight-list.component.html',
  styleUrls: ['./flight-list.component.css']
})
export class FlightListComponent {

  @Input() flights:Flight[]
  flightService:FlightServiceService=new FlightServiceService();
  flightUpdate:Flight=new Flight();

  constructor( private router: Router){ 
    this.flights=[{
      "flightNumber": "abc123456",
      "origin": "bangalore",
      "destination": "delhi",
      "flightDate":"10/11/2020"
    },
    {
      "flightNumber": "pqr123456",
    "origin": "mumbai",
    "destination": "bangalore",
    "flightDate":"22/09/2022"
    }];
  }

  ngOnInit(): void {


  }
updateFlight(flightNumber: string|undefined){
  this.router.navigate(['update-flight',flightNumber]);
  this.flightUpdate=this.flightService.getFlightByflightNumber("flightNumber");

  //update flight

  //this.flights=this.flightService.updateFlight(this.flightUpdate)

}

}




<h2>Flight List</h2>
<table class="table table-stripped">
    <thead>
        <tr>
            <th>Fight Number</th>
            <th>Origin</th>
            <th>Destination</th>
            <th>Flight Date</th>
            <th>Actions</th>
        </tr>
    </thead>
    <tbody>
        <tr *ngFor = "let flight of flights">
            <td> {{flight.flightNumber}} </td>
            <td> {{flight.origin}} </td>
            <td> {{flight.destination}} </td>
            <td> {{flight.flightDate}} </td>
            <td>
                <button (click)="showDialog = !showDialog" class="btn btn-info">Update</button>
                <br>
                <app-dialog [(visible)]="showDialog" title="update flight" data-popup="width:400;hieght:350">
                    <button (click) = "showDialog = !showDialog" class ="btn btn-info">Save
                    </button>

                </app-dialog>
            </td>       
        </tr>
    </tbody>
</table>
