import { Component, OnInit } from '@angular/core';
import { Flight } from '../flight';
import { FlightServiceService } from '../services/flight-service.service'
import { Router } from '@angular/router';


@Component({
  selector: 'app-create-flight',
  templateUrl: './create-flight.component.html',
  styleUrls: ['./create-flight.component.css']
})

export class CreateFlightComponent implements OnInit {
  flight: Flight ={
    flightId:0,
    origin: "",
    destination: "",
    flightdate: "",
    fare: {
        fareId: 0,
        fare: "",
        currency: "",
    },
    inventory: {
        id: 0,
        count: 0
    }
}
  constructor(private flightService: FlightServiceService, private router: Router) {}

  ngOnInit(): void {
    
    
  }

  saveFlight(){
    this.flightService.addFlight(this.flight).subscribe(data => {
      console.log(data);
      this.flight = new Flight();
      this.router.navigate(['/flight-list'])
  },
  error => console.log(error));}
  

  onSubmit(){
    console.log(this.flight);
    this.saveFlight();
  }


}
<div class="col-md-6 offset-md-3">
    <h3>Create Flight</h3>
<form name="createFlight">
    <form (ngSubmit) = "onSubmit()">
    
        <!--<div class="form-group">
            <label> Flight Number</label>
            <input type="text" class ="form-control" id = "flightNumber"
                [(ngModel)] = "flight.flightId" name = "flightNumber">
        </div>-->
    
        <div class="form-group">
            <label> Origin</label>
            <input type="text" class ="form-control" id = "origin"
                [(ngModel)] = "flight.origin" name = "origin" required>
                <span ng-show="createFlight.origin.$touched && createFlight.origin.$invalid">The origin is required.</span>
        </div>
    
        <div class="form-group">
            <label> Destination</label>
            <input type="text" class ="form-control" id = "Destination"
                [(ngModel)] = "flight.destination" name = "Destination">
        </div>
        <div class="form-group">
            <label> Flight Date</label>
            <input type="date" class ="form-control" id="flightDate" [(ngModel)] = "flight.flightdate" name="flightDate">
            
        </div>
        <div class="form-group">
            <label> Fare </label>
            <input type="text" class ="form-control" id = "Fare"
                [(ngModel)] = "flight.fare.fare" name = "Fare">
        </div>
        <div class="form-group">
            <label> Currency </label>
            <input type="text" class ="form-control" id = "FareCurrency"
                [(ngModel)] = "flight.fare.currency" name = "FareCurrency">
        </div>
        
        
        <div class="form-group">
            <label> No. of  Seats </label>
            <input type="text" class ="form-control" id = "No.ofSeats"
                [(ngModel)] = "flight.inventory.count" name = "No.ofSeats">
        </div>
    
        <button class = "btn btn-success" type ="submit">Submit</button>
</form>
</form>
</div>
