import { Component } from '@angular/core';
import { Router } from '@angular/router';

import { Flight } from '../flight';

@Component({
  selector: 'app-book',
  templateUrl: './book.component.html',
  styleUrls: ['./book.component.css']
})
export class BookComponent {

  //book: bookdetails[];
  flight: Flight=new Flight();
  flights :Flight[];



  constructor( private router: Router){
    this.flights=[]

  }



}
<div>
    <div class="container-md position-absolute top-50 start-50 translate-middle align-text-top">
        <table class="table table-borderless">
            <thead>
            <tr>
                <th>Origin</th>
                <th>Destination</th>
                <th>Flight Date</th>

            </tr>
            </thead>
            <tr>
                <td> {{flight.origin}} Bangalore </td>
                <td> {{flight.destination}} Delhi </td>
                <td> {{flight.flightdate}} 2023/01/17</td>
                <td>
        </table>

    </div>
    <div class="mx-auto position-absolute bottom-0 start-50 translate-middle-x">
        <button class="btn btn-primary align-text-bottom" type="button">Add Passenger Details</button>
    </div>
</div>
