import { Component, OnInit } from '@angular/core';
import { Book } from 'src/app/book';
import { Checkin } from 'src/app/checkin';
import { Passenger } from 'src/app/passenger';
import { CheckinService } from 'src/app/services/checkin.service';

@Component({
  selector: 'app-check-in',
  templateUrl: './check-in.component.html',
  styleUrls: ['./check-in.component.css']
})
export class CheckInComponent implements OnInit {
  passenger:Passenger[]=[]
  book:Book=new Book();
  checkin: Checkin={
    id: 0,
	  seatNumber:"",
	  checkInTime: "",
    book:{
        bookingId:0,
        flightNumber:0,
        flightDate:"",
        lastName:"",
        firstName:""
    }
  }
  checkins:Checkin[];
  constructor(private checkInService:CheckinService){
    this.checkins=[]
  }

  ngOnInit(): void {

  }
  private addCheckInDetails(){
    this.checkInService.addCheckIn(this.checkin).subscribe(data=>{
      console.log(data);
      this.checkin=new Checkin();
    })
  }
  private saveCheckin(){
    this.checkInService.getCheckInDetailsToSaveCheckInData(this.checkin.book.firstName,this.checkin.book.lastName,this.checkin.book.flightNumber,this.checkin.book.flightDate,this.checkin.book.bookingId).subscribe(data=>{
      console.log(data);      
      this.checkins=data;
      console.log(this.checkins);
      this.addCheckInDetails();
    },error=>console.log(error))
  }

  onSubmit(){
    this.saveCheckin();
    // this.addCheckInDetails()
  }
}
